import React from 'react';
interface FormatFn {
    (value: number): string | number;
}
interface Props extends React.HTMLAttributes<HTMLElement> {
    number: number;
    initial?: number;
    fps?: number;
    duration?: number;
    format?: FormatFn;
    component?: string;
}
declare const AnimatedNumber: React.FC<Props>;
export default AnimatedNumber;
